class Counter extends Component {
    state={
        count: 0
    }
    render() { 
        return(
            <div>
                <h1>{this.formatCount()}</h1>
                <button>Increament</button>
            </div>
        );
    }

    formatCount(){
        const {count} = this.state
        return count===0? <h1>Zero</h1> : count;
        //return this.state.count===0? 'Zero' : this.state.count;
    }
}

export default Counter;
